    private void jButton17ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton17ActionPerformed
        valor2 = Double.parseDouble(txtResultado.getText());
        if("soma".equals(sinal)){
            txtResultado.setText(String.valueOf(valor1+valor2));
        }
        if("subtracao".equals(sinal)){
            txtResultado.setText(String.valueOf(valor1-valor2));
        }
        if("divisao".equals(sinal)){
            txtResultado.setText(String.valueOf(valor1/valor2));
        }
        if("multiplicacao".equals(sinal)){
            txtResultado.setText(String.valueOf(valor1*valor2));
        }
        if("inverso".equals(sinal)){
            txtResultado.setText(String.valueOf(1/valor1));
        }
        if("raiz".equals(sinal)){
            txtResultado.setText(String.valueOf(sqrt(valor1)));
        }
       if("percentagem".equals(sinal)){
           txtResultado.setText(String.valueOf(valor1*(valor2/100)));
       } 
    }//GEN-LAST:event_jButton17ActionPerformed

